let prdtId = $('#prdtId').val();

function displayReview(res) {

    const content = res.map((item)=> {
        return (`
            <article class="comment-area">
                
                <div class='comment-area--user'>
                    <img src="<?= PathGenerator::generateImgPath('user.svg') ?>" alt="icon-user"> 
                </div>
        
                <div class="comment-area--trees-wrapper">
                    lolodzoqlqozdoq
                </div>

                <p class="comment-area--content">
                    Blalalalallalala
                </p>

            </article> 
        `)
    })


    $('#review').html(content);


    $(".pagination").click((e)=>{
        e.preventDefault();

        const page = $(e.currentTarget).attr('href');

        $.ajax({
            url: MAIN_PATH + "/product/review/pagination" + prdtId,
            method:"POST",
            data: {page},
            dataType: "json"
        })
        .done((res)=> {
            console.log(res);
            displayReview(res);
        })
        
    })

}


$.ajax({
    url : MAIN_PATH + "/product/review/" + prdtId,
    dataType:"json"
})
.done((res)=> {
    console.log(res);
    displayReview(res);
})





