let prdtId=$("#prdtId").val();function displayReview(a){const e=a.map(a=>`\n            <article class="comment-area">\n                \n                <div class='comment-area--user'>\n                    ${a.firstname} ${a.lastname}\n                </div>\n        \n                <div class="comment-area--trees-wrapper">\n                    ${a.rating} / 5 &#x1f384;\n                </div>\n\n                <p class="comment-area--content">\n                    ${a.comment}\n                    <span class="comment-area--date">${a.date}</span>\n                </p>\n\n            </article> \n        `);$("#review").html(e),$(".pagination").click(a=>{a.preventDefault(),$(".pagination").css("color","white"),$(a.currentTarget).css("color","#565656");const e=$(a.currentTarget).attr("href");$.ajax({url:MAIN_PATH+"/product/review/pagination"+prdtId,method:"POST",data:{page:e},dataType:"json"}).done(a=>{console.log(a),displayReview(a)})})}$.ajax({url:MAIN_PATH+"/product/review/"+prdtId,dataType:"json"}).done(a=>{console.log(a),displayReview(a)});